import React, { useState, useEffect} from 'react'
import './FinanceProblems.css';

const FinanceProblems = () => {

    const [questions, setQuestion] = useState([])
    const [answers, setAnswers] = useState([])
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [selectedAnswer, setSelectedAnswer] = useState(null)

    useEffect(() => {
        const fetchQuestion = async () => {
            try {
                const response = await fetch('http://localhost:4000/api/questions/Finance')
                const data = await response.json()
                console.log('Full log data:', data[0])
                if (data.length > 0) {
                    setQuestion(data)
                    setAnswers(data[0].answers)
                }
            } catch (error) {
                console.error('Error fetching question:', error)
            }
        }
        fetchQuestion()
    }, [])

    const currentQuestion = question[currentQuestionIndex]

    const handleAnswerChange = (event) => {
        setSelectedAnswer(Number(event.target.value))
    }
    const handleSubmit = async (event) => {
        event.preventDefault()
        try {
            const response = await fetch('http://localhost:4000/api/check-answer', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    question_id: question.id,
                    answer_id: selectedAnswer
                })
            })
            const data = await response.json()
            console.log('Response:', data)
        } catch (error) {
            console.error('Error submitting answer:', error)
        }
    }

    const handleNextQuestion = () => {
        if (currentQuestionIndex < question.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1)
            setSelectedAnswer(null)
        } else {
            console.log('No more questions available.')
        }
    }

    return (
        <div className="finance-container">
            <h1>Finance</h1>
            <div className="finance-boxes">
                <div className="financebox-one">
                    <h2>Question:</h2>
                    {question ? <p>{question.question}</p> : <p>Loading...</p>}
                </div>
                <div className="financebox-two">
                    <h2>Answer Choices</h2>
                    {answers.length > 0 ? (
                        answers.map((answer) => (
                            <div key={answer.id}>
                                <input
                                    type='radio'
                                    value={answer.id}
                                    checked={selectedAnswer === answer.id}
                                    onChange={handleAnswerChange}
                                />
                                <label>{answer.text}</label><br />
                            </div>
                        ))
                    ) : (
                        <p>Loading answers...</p>
                    )}
                </div>
                <button className="submit-problem" onClick={handleSubmit}>Submit Answer</button>

                {Array.isArray(question) && question.length > 1 && currentQuestionIndex < question.length - 1 && (
                    <button className="next-question" onClick={handleNextQuestion}>
                        Next Question
                    </button>
                )}

            </div>
        </div>
    );
};

export default FinanceProblems;
