import React, { useState } from 'react';
import { useNavigate} from 'react-router-dom';
import Profile from "../img/profile.png";
import Setting from '../img/setting.png';
import './Navbar.css';


const Navbar = () => {
    const [isNavOpen, setNavOpen] = useState(false)
    const navigate = useNavigate();

    const toggleNav = () => {
        setIsNavOpen(!isNavOpen);
    };

    const handleNavigation = (path) => {
        navigate(path);
        setIsNavOpen(false); // Close the mobile menu after navigation
    };

    const handleSettings = () => {
        navigate('/settings');
    }

    const handleProfile = () => {
        navigate('/profile');
    }

    return (
        < div className="navbar">
            <div className="menu" onClick={toggleNav}>
                <div className={`line ${isNavOpen ? 'open' : ''}`}></div>
                <div className={`line ${isNavOpen ? 'open' : ''}`}></div>
                <div className={`line ${isNavOpen ? 'open' : ''}`}></div>
            </div>
            <div className="nav-links">
                <img src={Setting} onClick={handleSettings} />  
                <img src={Profile} onClick={handleProfile} />
            </div>

            <div className={`nav-links ${isNavOpen ? 'open' : ''}`}>
                <button onClick={() => handleNavigation("/dashboard")}>Workout Log</button>
                <button onClick={() => handleNavigation("/calorie-tracker")}>Calorie Tracker</button>
                <button onClick={() => handleNavigation("/progress-report")}>Progress Report</button>
                <button onClick={() => handleNavigation("/my-challenges")}>Challenges</button>
                <button onClick={() => handleNavigation("/goals")}>Goals</button>
                <button onClick={() => handleNavigation("/profile")}>Profile</button>
            </div>
        </div>
    ) 

}

export default Navbar;
