import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
// Import individual components to avoid dependency issues
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Profile from "../img/profile.png";
import 'bootstrap/dist/css/bootstrap.min.css';

const NavigationBar = () => {
    const [expanded, setExpanded] = useState(false);
    const navigate = useNavigate();

    const handleNavigation = (path) => {
        navigate(path);
        setExpanded(false);
    };

    const handleProfile = () => {
        navigate('/profile');
    };

    return (
        <Navbar bg="light" expand="lg" expanded={expanded} onToggle={setExpanded}>
            <Container>
                <Navbar.Brand>My App</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Item>
                            <Nav.Link onClick={() => handleNavigation("/dashboard")}>Introduction</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link onClick={() => handleNavigation("/project-motivation")}>Project Motivation</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link onClick={() => handleNavigation("/coding")}>Coding Lessons</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link onClick={() => handleNavigation("/finance")}>Finance Lessons</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link onClick={() => handleNavigation("/language")}>Language Lessons</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link onClick={() => handleNavigation("/achievments")}>Achievments</Nav.Link>
                        </Nav.Item>
                    </Nav>
                </Navbar.Collapse>
                <div className="d-flex align-items-center">
                    <img 
                        alt="ProfileIMG" 
                        src={Profile} 
                        onClick={handleProfile}
                        style={{ 
                            cursor: 'pointer',
                            width: '40px',
                            height: '40px',
                            borderRadius: '50%'
                        }} 
                    />
                </div>
            </Container>
        </Navbar>
    );
};

export default NavigationBar;
