import React, { useState, useEffect } from 'react';
import AceEditor from 'react-ace';
import axios from 'axios';
import 'ace-builds/src-noconflict/mode-python';
import 'ace-builds/src-noconflict/theme-github';
import 'ace-builds/webpack-resolver';
import './QuestionSandbox.css'; // We'll create this for styling

const QuestionSandbox = () => {
    const [code, setCode] = useState('# Write your Python code here');
    const [output, setOutput] = useState('');
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(null);
    const [loading, setLoading] = useState(false);
    const [selectedAnswer, setSelectedAnswer] = useState(null);
    const [feedback, setFeedback] = useState(null);

    // Fetch categories when component mounts
    useEffect(() => {
        fetchCategories();
    }, []);

    // Fetch questions when category changes
    useEffect(() => {
        if (selectedCategory) {
            fetchQuestions(selectedCategory);
        }
    }, [selectedCategory]);

    const fetchCategories = async () => {
        try {
            setLoading(true);
            const response = await axios.get('http://localhost:4000/api/categories');
            setCategories(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching categories:', error);
            setLoading(false);
        }
    };

    const fetchQuestions = async (category) => {
        try {
            setLoading(true);
            const response = await axios.get(`http://localhost:4000/api/questions/${category}`);
            setQuestions(response.data);
            setCurrentQuestion(response.data.length > 0 ? response.data[0] : null);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching questions:', error);
            setLoading(false);
        }
    };

    const handleCategoryChange = (e) => {
        setSelectedCategory(e.target.value);
        setFeedback(null);
        setSelectedAnswer(null);
    };

    const handleQuestionChange = (questionId) => {
        const question = questions.find(q => q.id === parseInt(questionId));
        setCurrentQuestion(question);
        setFeedback(null);
        setSelectedAnswer(null);
    };

    const handleRunCode = async () => {
        try {
            setLoading(true);
            const response = await axios.post('http://localhost:4000/api/run-code', { code });
            setOutput(response.data.output || response.data.error);
            setLoading(false);
        } catch (error) {
            console.error('Error running code:', error);
            setOutput('Error: Failed to execute code');
            setLoading(false);
        }
    };

    const handleAnswerSubmit = async (answerId) => {
        if (!currentQuestion) return;

        try {
            setLoading(true);
            const response = await axios.post('http://localhost:4000/api/check-answer', {
                question_id: currentQuestion.id,
                answer_id: answerId
            });

            setFeedback({
                isCorrect: response.data.is_correct,
                explanation: response.data.explanation
            });

            setSelectedAnswer(answerId);
            setLoading(false);
        } catch (error) {
            console.error('Error checking answer:', error);
            setLoading(false);
        }
    };

    return (
        <div className="question-sandbox-container">
            <h1>Python Practice Questions</h1>

            <div className="category-selector">
                <label htmlFor="category">Select Category: </label>
                <select 
                    id="category" 
                    value={selectedCategory} 
                    onChange={handleCategoryChange}
                    disabled={loading}
                >
                    <option value="">Select a category</option>
                    {categories.map(category => (
                        <option key={category.id} value={category.name}>
                            {category.name}
                        </option>
                    ))}
                </select>
            </div>

            {selectedCategory && questions.length > 0 && (
                <div className="question-selector">
                    <label htmlFor="question">Select Question: </label>
                    <select 
                        id="question" 
                        onChange={(e) => handleQuestionChange(e.target.value)}
                        disabled={loading}
                    >
                        {questions.map(question => (
                            <option key={question.id} value={question.id}>
                                {question.question_text.substring(0, 40)}...
                            </option>
                        ))}
                    </select>
                </div>
            )}

            <div className="content-area">
                <div className="question-area">
                    {currentQuestion ? (
                        <>
                            <div className="question-text">
                                <h2>Question</h2>
                                <p>{currentQuestion.question_text}</p>
                                <p className="difficulty">
                                    Difficulty: {currentQuestion.difficulty}
                                </p>
                            </div>

                            <div className="answers">
                                <h3>Choose an answer:</h3>
                                {currentQuestion.answers.map(answer => (
                                    <div 
                                        key={answer.id} 
                                        className={`answer-option ${selectedAnswer === answer.id ? 
                                            (feedback?.isCorrect ? 'correct' : 'incorrect') : ''}`}
                                    >
                                        <button 
                                            onClick={() => handleAnswerSubmit(answer.id)}
                                            disabled={selectedAnswer !== null || loading}
                                        >
                                            {answer.text}
                                        </button>
                                    </div>
                                ))}
                            </div>

                            {feedback && (
                                <div className={`feedback ${feedback.isCorrect ? 'correct' : 'incorrect'}`}>
                                    <h3>{feedback.isCorrect ? 'Correct!' : 'Incorrect'}</h3>
                                    <p>{feedback.explanation}</p>
                                </div>
                            )}
                        </>
                    ) : (
                        <div className="no-question">
                            {selectedCategory ? 'No questions available for this category.' : 'Select a category to view questions.'}
                        </div>
                    )}
                </div>

                <div className="sandbox-area">
                    <h2>Code Sandbox</h2>
                    <AceEditor
                        mode="python"
                        theme="github"
                        onChange={setCode}
                        name="code_editor"
                        editorProps={{ $blockScrolling: true }}
                        width="100%"
                        height="300px"
                        value={code}
                        setOptions={{
                            useWorker: false
                        }}
                    />
                    <button 
                        onClick={handleRunCode}
                        disabled={loading}
                        className="run-button"
                    >
                        {loading ? 'Running...' : 'Run Code'}
                    </button>

                    <div className="output-area">
                        <h3>Output:</h3>
                        <pre>{output}</pre>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CodeSandbox;
