import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Profile from "../img/profile.png";
import './Navbar.css'; // Keep your existing styles for now

const Navbar = () => {
    const [isNavOpen, setIsNavOpen] = useState(false);
    const navigate = useNavigate();

    const toggleNav = () => {
        setIsNavOpen(!isNavOpen);
    };

    const handleNavigation = (path) => {
        navigate(path);
        setIsNavOpen(false);
    };

    const handleProfile = () => {
        navigate('/profile');
    };

    // Bootstrap-inspired classes without requiring the actual library
    return (
        <nav className="navbar navbar-expand-lg">
            <div className="container">
                {/* Hamburger menu */}
                <button 
                    className={`navbar-toggler ${isNavOpen ? 'collapsed' : ''}`} 
                    type="button" 
                    onClick={toggleNav}
                    aria-controls="navbarNav" 
                    aria-expanded={isNavOpen} 
                    aria-label="Toggle navigation"
                >
                    <div className={`line ${isNavOpen ? 'open' : ''}`}></div>
                    <div className={`line ${isNavOpen ? 'open' : ''}`}></div>
                    <div className={`line ${isNavOpen ? 'open' : ''}`}></div>
                </button>

                {/* Brand/logo area - you can add one if needed */}

                {/* Navigation links */}
                <div className={`collapse navbar-collapse ${isNavOpen ? 'show' : ''}`} id="navbarNav">
                    <ul className="navbar-nav me-auto">
                        <li className="nav-item">
                            <button className="nav-link" onClick={() => handleNavigation("/dashboard")}>Introduction</button>
                        </li>
                        <li className="nav-item">
                            <button className="nav-link" onClick={() => handleNavigation("/project-motivation")}>Project Motivation</button>
                        </li>
                        <li className="nav-item">
                            <button className="nav-link" onClick={() => handleNavigation("/coding")}>Coding Lessons</button>
                        </li>
                        <li className="nav-item">
                            <button className="nav-link" onClick={() => handleNavigation("/finance")}>Finance Lessons</button>
                        </li>
                        <li className="nav-item">
                            <button className="nav-link" onClick={() => handleNavigation("/language")}>Language Lessons</button>
                        </li>
                        <li className="nav-item">
                            <button className="nav-link" onClick={() => handleNavigation("/achievments")}>Achievments</button>
                        </li>
                    </ul>
                </div>

                {/* Profile image */}
                <div className="profile-container">
                    <img 
                        alt="ProfileIMG" 
                        src={Profile} 
                        onClick={handleProfile} 
                        className="profile-image"
                    />
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
